    IMPORT_C TInt UnLock(TInt64 aPos,TInt64 aLength) const;
    IMPORT_C TInt Seek(TSeek aMode,TInt64& aPos) const;
    IMPORT_C TInt Flush();
    IMPORT_C TInt Size(TInt64& aSize) const;
