class CHeartbeatRunner: public MBeating
    {
public:
    // Implement MBeating
    void Beat();
    void Synchronize();

    // Start timer
    void StartTimer();
private:
    CHeartbeat* iHeartbeat;
};

void CHeartbeatRunner::StartTimer()
    {
    iHeartbeat=CHeartbeat::NewL(0); // neutral priority
    iHeartbeat->Start(ETwelveOClock,this); // 1 second intervals
    }
