section.codeblock:
// Get pointer to client threads DThread object
iClient=&Kern::CurrentThread();

// Open a reference on client thread so it's control block can't disappear until
// this driver has finished with it.
// Note, this call to Open can't fail since its the thread we are currently running in
((DObject*)iClient)->Open();

EXPORT_C TInt DObject::RequestUserHandle(DThread* aThread, TOwnerType aType)
    {
    (void)aThread;
    (void)aType;
    return KErrNone;
    }

TInt DMyChannel::RequestUserHandle(DThread* aThread, TOwnerType aType)
    {
    if (aType!=EOwnerThread || aThread!=iClient)
        {
        return KErrAccessDenied;
        }
    return KErrNone;
    }
