// 1. Get the cryptography strength.

TCrypto::TStrength strength = TCrypto::Strength();


// 2. Determine the cryptographic strength.

switch (strength)
    {
    case TCrypto::EWeak:
        // Using 56 bit key strength.
        break;
    case TCrypto::EStrong:
        // Using 512 bit key strength.
        break;
    default:
    }
