example.codeblock:
    // RFile file;
    RFile64 file;
    TInt err = file.Open(TheFs, _L(“BIGFILE.BIG”), EFileWrite);
    if(err != KErrNone)
        {
        // unrelated error
        return err;
        }

    // Large file access is now supported!
    // TInt seekPos = 0;
    Tint64 seekPos = 0;

    // Seek the current position. This can be greater than 2GB.
    err = file.Seek(ESeekEnd, seekPos);
    if(err != KErrNone)
        {
        return err;
        }
